import React, { useEffect, useState, useRef } from 'react';
import { fetchHistory, sendMessage } from './api';

export default function App(){
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesRef = useRef();

  useEffect(()=> {
    loadHistory();
  }, []);

  async function loadHistory(){
    try {
      const data = await fetchHistory();
      setMessages(data || []);
      scrollToBottom();
    } catch (err) {
      console.error(err);
      // show nothing, user can still chat
    }
  }

  function scrollToBottom(){
    setTimeout(()=> {
      const el = messagesRef.current;
      if(el) el.scrollTop = el.scrollHeight;
    }, 100);
  }

  async function handleSend(e){
    e?.preventDefault();
    if (!text.trim()) return;
    const userText = text.trim();
    setText('');
    // show optimistic user's message
    setMessages(prev => [...prev, { id: `temp-${Date.now()}`, role: 'user', text: userText }]);
    scrollToBottom();

    setLoading(true);
    try {
      const res = await sendMessage(userText);
      // res.history is authoritative
      setMessages(res.history || []);
      scrollToBottom();
    } catch (err) {
      console.error(err);
      // Optionally show an error message (kept minimal)
      setMessages(prev => [...prev, { id: `err-${Date.now()}`, role: 'ai', text: 'Failed to get AI reply. See console.' }]);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="app">
      <div className="header">
        <div>AI Chat — {`YourName`}</div>
        <div className="small">Saved history on backend</div>
      </div>

      <div className="messages" ref={messagesRef}>
        {messages.map(m => (
          <div key={m.id} className={`message ${m.role === 'user' ? 'user' : 'ai'}`}>
            <div>{m.text}</div>
            <div className="meta">{m.role} • {new Date(m.createdAt || Date.now()).toLocaleString()}</div>
          </div>
        ))}
      </div>

      <form className="inputRow" onSubmit={handleSend}>
        <input
          type="text"
          placeholder="Type a message..."
          value={text}
          onChange={e => setText(e.target.value)}
          disabled={loading}
        />
        <button type="submit" disabled={loading}>{loading ? '...' : 'Send'}</button>
      </form>
    </div>
  );
}
